# Groupes de points
mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes([(12, 6), (2, 8), (8, 12), (6, 2), (12, 0)], color='red')
mv.showNodes([(10, 0), (8, 7), (14, 9), (10, 14), (3, 9), (4, 0)], color='blue')

