# Points fantômes
mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes([(12.5, 6.5)], color='red', size=15)
mv.showNodes([(5.666, 4.12)], color='blue', size=20)

mv.writeLog("Points fantômes")
mv.show()

### VISUALISATION DE CHEMINS
# Chemin simple
origin = (14,0)
chemin = "RUULUUUUURRRUULD"

mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes(piecesOfCheese)
mv.showPath(origin, chemin, color='red')

mv.writeLog("Chemin simple 1")
mv.show()

# Chemin simple 2
chemin = [(14,0), (14,1), (13,1), (13,2), (12,2), (12,3), (12,4), (11,4), (10,4)]

mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes(piecesOfCheese)
mv.showPath(chemin, color='green')

mv.writeLog("Chemin simple 2")
mv.show()

# Chemin fantômes : type metaGraph
chemin = [(12, 6), (10, 4), (4, 10), (10, 0), (8, 7), (7, 0), (7, 14)]

mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes(piecesOfCheese)
mv.showPath(chemin, color='red', size=20)

mv.writeLog("Chemin fantômes")
mv.show()

# Chemin composé 1
origin = (14,0)
chemins = ["RUUL", "UU", "UUU", "RRRUULD"]

mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes(piecesOfCheese)
mv.showPath(origin, chemins, color='green')

mv.writeLog("Chemin composé 1")
mv.show()

# Chemin composé 2
chemins = [[(14,0), (14,1), (13,1)], [(13,1), (13,2), (12,2)], [(12,2), (12,3), (12,4), (11,4), (10,4)]]

mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes(piecesOfCheese)
mv.showPath(chemins, color='green')

mv.writeLog("Chemin composé 2")
mv.show()

# Opposition de chemins
# On peut en ajouter une infinité, et varier les types de chemins
chemins1 = [[(14,0), (14,1), (13,1)], [(13,1), (13,2), (12,2)], [(12,2), (12,3), (12,4), (11,4), (10,4)]]

origin2 = (14,0)
chemins2 = ["RUUL", "UU", "UUU", "RRRUULD"]

mv = MazeViewer(mazeMap, mazeWidth, mazeHeight)
mv.showNodes(piecesOfCheese)
mv.showPath(chemins1, color='red', size=15)
mv.showPath(origin2, chemins2, color='blue', size=10)

mv.writeLog("Opposition de chemins")
mv.show()

